#ifndef OCLACCSUBTARGET_H
#define OCLACCSUBTARGET_H

#include "llvm/Target/TargetSubtargetInfo.h"
#include <string>

#define GET_SUBTARGETINFO_HEADER
#include "OCLAccGenSubtargetInfo.inc"

namespace llvm {
class StringRef;

class OCLAccSubtarget : public OCLAccGenSubtargetInfo {
  private:
    virtual void anchor();
    bool HasF1;
    bool HasF2;
    bool HasF3;
    bool HasF4;
    bool HasF5;
  public:
    OCLAccSubtarget(const std::string &TT, const std::string &CPU,
        const std::string &FS);

    //Generated by TableGen
    void ParseSubtargetFeatures(StringRef CPU, StringRef FS);

    bool hasF1() const { return HasF1; }
    bool hasF2() const { return HasF2; }
    bool hasF3() const { return HasF3; }
    bool hasF4() const { return HasF4; }
    bool hasF5() const { return HasF5; }
};
} //end namespace llvm



#endif /* OCLACCSUBTARGET_H */
