#ifndef OCLACCSUBTARGET_H
#define OCLACCSUBTARGET_H

#include "llvm/Target/TargetSubtargetInfo.h"
#include <string>

#define GET_SUBTARGETINFO_HEADER
#include "OCLAccGenSubtargetInfo.inc"

namespace llvm {
class StringRef;

class OCLAccSubtarget final : public OCLAccGenSubtargetInfo {
  private:
    virtual void anchor();

    bool Virtex5 = false;
    bool Stratix5 = false;
  public:
    OCLAccSubtarget(const std::string &TT, const std::string &CPU,
        const std::string &FS);

    // Generated by TableGen
    void ParseSubtargetFeatures(StringRef CPU, StringRef FS);

    // Xilinx
    bool isVirtex5() const { return Virtex5; }
    // Altera
    bool isStratix5() const { return Stratix5; }
};
} //end namespace llvm



#endif /* OCLACCSUBTARGET_H */
